const validate = values => {
    const errors = {};

    if (!values.city) {
      errors.city = "Required";
    }

    if (!values.dob) {
      errors.dob = "Required";
    }

    if (!values.sex) {
      errors.sex = "Required";
    }

  //  console.log("errord " + values.sex)


    return errors;
  };

  export default validate;
