import React, { Component } from "react";
import validate from "../../Utils/validations/form2Validations";
import renderField from "./RenderField";
import { reduxForm, Field } from "redux-form";
import RenderDateTimePicker from "./RenderDatePicker";
import RadioButtons from "./RadioButton";

import "react-datepicker/dist/react-datepicker.css";

import "react-widgets/dist/css/react-widgets.css";

class Form2 extends Component {
  constructor() {
    super();

    this.state = {
      startDate: new Date("1997-06-06"),
      countries: [
        "South Africa",
        "Zambia",
        "Botswana",
        "Swaziland",
        "Lesotho",
        "Tanzania",
        "Mozambique"
      ]
    };
  }

  render() {
    const { previousPage, handleSubmit } = this.props;

    return (
      <form onSubmit={handleSubmit}>
        {/* <div className="field"> */}

        <Field
          class="Field"
          name="city"
          component={renderField}
          type="text"
          label="City"
          placeholder="City"
        />
        {/* </div> */}

        <Field
          name="dob"
          showTime={true}
          component={RenderDateTimePicker}
          label="Dob"
        />

        <label>Browser Select</label>
        <select class="browser-default">
          <option value="">Choose your option</option>
          <option value="1">Option 1</option>
          <option value="2">Option 2</option>
          <option value="3">Option 3</option>
        </select>
        {/* <Field
          name="options"
          countries={this.state.countries}
          component="select"
          type="select"
          label="Country"
          value="country"
        > */}
        <select name="" id="">
          <option />
          {this.state.countries.map(country => (
            <option value={country}> {country} </option>
          ))}
        </select>
        {/* </Field> */}
        <div>
          <label>Sex</label>
          <div>
            <Field
              name="sex"
              component={RadioButtons}
              type="radio"
              label="male"
              value="male"
            />
            <Field
              name="sex"
              component={RadioButtons}
              type="radio"
              label="female"
              value="female"
            />
          </div>
        </div>
        <div className="row space-between m1">
          <button
            type="button"
            className="btn waves-effect waves-light col m6"
            onClick={previousPage}
          >
            Previous
          </button>
          <button className="btn col m6" type="submit">
            Next
          </button>
        </div>
      </form>
    );
  }
}

export default reduxForm({
  form: "form2", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form2);
