import React, { Component } from "react";
import PropTypes from "prop-types";
import { reduxForm, Field } from "redux-form";
import validate from "../../Utils/validations/form1Validations";
import renderField from "./RenderField";
import { validateEmail } from "../../Utils/validations/asyncValidation";
import { SubmissionError } from "redux-form";

class Form1 extends Component {
  constructor() {
    super();
  }

  async handleSubmit(data) {
    try {
      const error = await validateEmail(data.email);
      console.log("dfsf", data.email);

      if (error.email) {
        throw new SubmissionError({
          error
        });
      }
    } catch (e) {
      console.log(e);
    }

    console.log(this.props.nextPage());
  }

  render() {
    console.log(this.state);
    const { handleSubmit } = this.props;
    return (
      <form onSubmit={handleSubmit(data => this.handleSubmit(data))}>
        <Field
          name="email"
          component={renderField}
          type="text"
          label="email"
          placeholder="Enter Email"
        />
        <Field
          name="firstName"
          component={renderField}
          type="text"
          label="Name"
          placeholder="Enter name"
        />

        <Field
          name="lastName"
          component={renderField}
          label="Surname"
          type="text"
          placeholder="Enter Surname"
        />
        <Field
          name="contact"
          component={renderField}
          type="text"
          label="Contact"
          placeholder="Enter Contacts"
        />
        <button type="submit" className="btn  col btn-block">
          Next
        </button>
      </form>
    );
  }
}

Form1.propTypes = {};

export default reduxForm({
  form: "form1", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form1);

