import React, { Component } from "react";

class form2 extends Component {
  constructor() {
    super();

    this.state = {
      country : ["South Africa" , "Zambia" , 'Botswana' , 
      'Swaziland' , "Lesotho" , "Tanzania" , 'Mozambique']
    };
  }

  setGender(event) {
    console.log(event.target.value);
  }

  render() {
    const {previousPage} = this.props;
    return (
      <div>
        <div>
          <div className="field">
            <label> City </label>
            <input placeholder="City" class="input" type="text" />
          </div>
          <select class="option">
            {
              this.state.country.map((country) =><option> {country} </option>)
            }
          </select>
          <div className="field">
            <label> date of birth </label>
            <input placeholder="Place of birth" class="input" type="text" />
          </div>
          <div onChange={this.setGender.bind(this)}>
            <label style={{ display: "block", margin: "1rem" }}> Gender </label>
            <input type="radio" value="MALE" name="gender" /> Male
            <input type="radio" value="FEMALE" name="gender" /> Female
          </div>
          <button type="button" className="previous" onClick={previousPage}>
          Previous
        </button>
        <button type="submit" className="next">
          Next
        </button>
        </div>
      </div>
    );
  }
}

form2.propTypes = {};

export default form2;
