import React, { Component } from "react";
import validate from "../../Utils/validations/form2Validations";
import renderField from "./RenderField";
import { reduxForm, Field } from "redux-form";
import moment from "moment";
// import momentLocaliser from 'react-widgets/lib/localizers/moment'
// import RenderDatePicker from './RenderDatePicker'
import DateTimePicker from "react-widgets/lib/DateTimePicker";
import "react-datepicker/dist/react-datepicker.css";
import renderDateTimePicker from "react-widgets/lib/DateTimePicker";

import "react-widgets/dist/css/react-widgets.css";

// momentLocaliser(moment)

class Form2 extends Component {
  constructor() {
    super();

    this.choiceDate = this.choiceDate.bind(this);

    this.state = {
      startDate: new Date("1997-06-06"),
      country: [
        "South Africa",
        "Zambia",
        "Botswana",
        "Swaziland",
        "Lesotho",
        "Tanzania",
        "Mozambique"
      ]
    };
  }

  choiceDate(date) {
    this.setState({
      startDate: date
    });
  }

  renderDateTimePicker = ({ input: { onChange, value }, showTime }) => (
    <DateTimePicker
      onChange={onChange}
      format="DD MMM YYYY"
      time={showTime}
      value={!value ? null : new Date(value)}
    />
  );

  startingDate() {
    var startDate = moment("1997-06-06").format();
    return startDate;
  }

  setGender(event) {
    console.log(event.target.value);
  }

  render() {
    const lastMonth = new Date();
    lastMonth.setMonth(lastMonth.getMonth() - 2);
    const value = 0;
    const { previousPage, handleSubmit } = this.props;
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <div className="field">
            <Field
              class="Field"
              name="city"
              component={renderField}
              type="city"
              label="City"
              placeholder="City"
            />
          </div>
          <select class="option">
            {this.state.country.map(country => (
              <option> {country} </option>
            ))}
          </select>

          <DateTimePicker disabled defaultValue={new Date()} />

          <div onChange={this.setGender.bind(this)}>
            <label style={{ display: "block", margin: "1rem" }}> Gender </label>
            <input type="radio" value="MALE" name="gender" /> Male
            <input type="radio" value="FEMALE" name="gender" /> Female
          </div>

          <button type="button" className="previous" onClick={previousPage}>
            Previous
          </button>
          <button type="submit" className="next">
            Next
          </button>
        </form>
      </div>
    );
  }
}

export default reduxForm({
  form: "Form2", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form2);
