import React, { Component } from "react";
import { reduxForm, Field } from "redux-form";
import validate from "../../Utils/validations/form4Validations";
import history from "../../routes/history";
import renderField from "./RenderField";
import { connect } from "react-redux";
import * as thunks from "../../actions/register";

class Form3 extends Component {
  handleSubmit = async data => {
    history.push("/dashboard");

    this.props.registerUser({
      ...this.props.form1,
      ...this.props.form2,
      ...this.props["object Object"]
    });
    console.log("data" + data);

    console.log({
      ...this.props.form.form1.values,
      ...this.props.form.form2.values,
      ...data
    });
  };

  render() {
    const { handleSubmit, pristine, previousPage, submitting } = this.props;
    return (
      <div>
        <form onSubmit={handleSubmit(this.handleSubmit)}>
          <Field
            class="input"
            type="password"
            component={renderField}
            label="Password"
            name="password"
            placeholder="Password"
          />
          <Field
            name="confirmPassword"
            component={renderField}
            type="password"
            label="confirm password"
            placeholder="Enter password"
          />

          <button type="button" className="previous" onClick={previousPage}>
            Previous
          </button>

          <button type="submit" disabled={pristine || submitting}>
            Submit
          </button>
        </form>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return { form: state.form };
}

const reduxForm3 = reduxForm({
  form: "Form4", //Form name is same
  destroyOnUnmount: false,
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form3);

function mapDispatchToProps(dispatch) {
  return {
    registerUser: data => dispatch(thunks.registerUser(data))
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(reduxForm3);
