import React, { Component } from "react";
import PropTypes from "prop-types";
import { reduxForm, Field } from "redux-form";
import validate from "../../Utils/validations/form1Validations";
import renderField from "./RenderField";
import { SubmissionError } from "redux-form";

class Form1 extends Component {
  constructor() {
    super();
  }

  handleSubmit(values) {
    const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));

    return sleep(1000).then(() => {
      // simulate server latency
      if (!["john", "paul", "george", "ringo"].includes(values.username)) {
        throw new SubmissionError({
          username: "User does not exist",
          _error: "Login failed!"
        });
      }
    });
    // console.log(this.props.nextPage());
  }

  render() {
    console.log(this.state);
    const { handleSubmit } = this.props;
    return (
      <form onSubmit={handleSubmit(data => this.handleSubmit(data))}>
        <h1>Aplicant information </h1>
        <div className="field">
          <Field
            name="email"
            component={renderField}
            type="text"
            label="email"
            placeholder="Enter Email"
          />
        </div>
        <div>
          <Field
            className="field"
            name="firstName"
            component={renderField}
            type="text"
            label="Name"
            placeholder="Enter name"
          />
        </div>
        <div>
          <Field
            className="Field"
            name="lastName"
            component={renderField}
            label="Surname"
            type="text"
            placeholder="Enter Surname"
          />
        </div>
        <div className="field">
          <Field
            class="Field"
            name="contact"
            component={renderField}
            type="text"
            label="Contact"
            placeholder="Enter Contacts"
          />
        </div>
        <div className="field">
          <Field
            class="Field"
            name="pioneerRefs"
            component={renderField}
            type="text"
            label="Pioneer Refs no"
            placeholder="Enter Refs no"
          />
        </div>
        <button type="submit" className="next">
          Next
        </button>
      </form>
    );
  }
}

Form1.propTypes = {};

export default reduxForm({
  form: "form1", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form1);

