import React, { Component } from "react";
import { connect } from "react-redux";
import Dialog from "./Dialog";
import Footer from "./Footer";
import * as quiers from "./../actions/queries";

class Users extends Component {
  constructor() {
    super();

    this.state = {
      isLoading: false
    };
  }
  componentDidMount() {
    this.setState({ isLoading: true });
    let interval = setInterval(() => {
      this.getMembers();
      this.setState({ isLoading: false });
      console.log(this.state.isLoading);
    }, 5000);

    clearInterval(interval);
  }

  getMembers() {
    console.log(this.props.userId);
    this.props.getMembers(this.props.userId);
    // console.log("id", this.props.userId);
  }

  render() {
    console.log("member", this.props.members);
    return (
      <div class="row">
        <div className="container">
          <h1>Currently memebers</h1>
          <div>
            {!this.state.isLoading
              ? this.props.members.map(member => (
                  <div
                    class="col card"
                    style={{ margin: "0rem", width: "100%", padding: "1rem" }}
                  >
                    <div style={{ width: "100%" }}>
                      <label> First Name</label>
                      <h6>{member.first_name}</h6>
                    </div>
                    <div>
                      <label> email</label>
                      <br />
                      <span>{member.email}</span>
                    </div>
                  </div>
                ))
              : "Loading..."}
          </div>
        </div>
      </div>
    );
  }
}

Users.propTypes = {};

function mapDispatchToProps(dispatch) {
  return {
    getMembers: id => dispatch(quiers.getMembers(id))
  };
}

// export default Users;

function mapStateToProps(state) {
  return {
    members: state.user.allMembers,
    userId: state.user.profile.id
  };
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Users);
