import axios from "axios";
import history from "../routes/history";
import * as actions from '../actionTypes/index'
import * as system from '../actions/system';
import jwtDecode from "jwt-decode";
const URL = "http://localhost:3001";


export const registerUser = (credentials) => {
  return async dispatch => {
    try {
      const res = await axios.post(`${URL}/signin`, credentials);
      dispatch({
        type: actions.AUTHENTICATED,
        payload: {
          auth: true
        }
      });
      localStorage.setItem("user", res.data.token);
      const decodedToken = jwtDecode(res.data.token);
      system.setProfile(decodedToken);
      history.push("/dashboard")
    } catch (error) {
      console.log(error);
    }
  };
};


export const login = (credentials) => {
  return async dispatch => {
    try {
      const res = await axios.post(`${URL}/login`, credentials);
      const decodedToken = jwtDecode(res.data.token);
      system.setProfile(decodedToken);
      localStorage.setItem("user", res.data.token);
      dispatch({
        type: actions.AUTHENTICATED,
        payload: {
          auth: true
        }
      });
      history.push("/dashboard")
      return
    } catch (e) {
      const data = !e.response ? null : e.response.data.error;
      dispatch({
        type: actions.AUTH_ERROR,
        payload: {
          error: data
        }
      });
      return
    }
  };
};
