import React, { Component } from "react";
import PropTypes from "prop-types";

class LoginForm extends Component {
  constructor() {
    super();

    this.state = {
      email: "",
      name: "",
      pioneerRefs: "",
      contact: "",
      surname: ""
    };
  }

  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <div className="field">
          <label> Email </label>
          <input
            class="input"
            onChange={event => this.handleChange(event)}
            name="email"
            type="text"
            placeholder="Enter Email"
          />
        </div>
        <div className="field">
          <label> Password </label>
          <input
            class="input"
            onChange={event => this.handleChange(event)}
            name="password"
            type="password"
            placeholder="Enter password"
          />
          <button style={{margin:"2rem"}}>Confirm </button>
        </div>
      </div>
    );
  }
}

LoginForm.propTypes = {};

export default LoginForm;
