import React from "react";

const renderRadio = ({ input, label, type, meta: { touched, error } }) => (
  <div>
    <label style={{ margin: "1rem" }}>{label}</label>
    <div className="field">
      <input
        lass="with-gap"
        name="group1"
        {...input}
        placeholder={label}
        type={type}
      />
      {touched && error && <span className="error">{error}</span>}
    </div>
  </div>
);

export default renderRadio;
