import React from "react";
import { Router, Route, Redirect } from "react-router-dom";
import store from "../store";
import { AUTHENTICATED } from "../actionTypes";
import jwtDecode from "jwt-decode";
import { setProfile } from "../actions/system";

const token = localStorage.getItem("user");

const authenticatedUser = () => {
  // let decodedToken;
  // if (token) {
  //   decodedToken = jwtDecode(token);
  //   let currentDate = Date.now() / 1000;
  //   if (decodedToken.lat > currentDate) {
  //     store.dispatch(setProfile(decodedToken));
  //     store.dispatch({
  //       type: AUTHENTICATED,
  //       payload: { auth: true }
  //     });

  //     return;
  //   }
  // }

  if (token) {
    const decodedToken = jwtDecode(token);
    store.dispatch(setProfile(decodedToken));
    store.dispatch({
      type: AUTHENTICATED,
      payload: { auth: true }
    });
    return true;
  }
};

// if (token) {
//   store.dispatch({ type: AUTHENTICATED, payload: { auth: true } });
// }

export const PrivateRoute = ({ component: Component, ...rest }) => {
  authenticatedUser();
  // var authenticated = store.getState().user.auth;
  // console.log("authllllllllllllllllllllllll", authenticated);
  return (
    <Route
      {...rest}
      render={props =>
        localStorage.getItem("user") ? (
          <Component {...rest} />
        ) : (
          <Redirect to="/" />
        )
      }
    />
  );
};
