var saIdParser = require('south-african-id-parser');

const validate = values => {
  const errors = {};

  if (!values.city) {
    errors.city = "Required";
  }

  if (values.Id) {
    var info = saIdParser.parse(values.Id);
    if (!info) {
      errors.id = "Required";
    }
  }

  if (!values.sex) {
    errors.sex = "Required";
  }

  //  console.log("errord " + values.sex)


  return errors;
};

export default validate;
