import React, { Component } from "react";
import Form1 from './forms/Form1';

class Register extends Component {
  constructor() {
    super();

    this.state = {
      page: 1,
      country: ["South Afica"]
    };
  }

  setGender(event) {
    console.log(event.target.value);
  }

  renderPage() {
    if (this.state.page == 1) {
      return (
        <div>
            <Form1/>
        </div>
      );
    }
    if (this.state.page == 2) {
      return (
        <div>
          <div className="field">
            <label> city </label>
            <input type="text" />
            </div>
            <select class="option">
              <option>Country </option>
            </select>
            <div className="field">
            <label> place of birth </label>
            <input type="text" />
            </div>
        </div>
      );
    }
  }

  changePage() {
    const name = "mo";
    this.setState({ page: this.state.page + 1 });
  }

  render() {
    return (
      <div class="register">
        {this.renderPage()}
        <button className="btn" onClick={() => this.changePage()}> next </button>
      </div>
    );
  }
}

Register.propTypes = {};

export default Register;
