import React, { Component } from "react";
import validate from "../../Utils/validations/form2Validations";
import renderField from "./RenderField";
import { reduxForm, Field } from "redux-form";
import moment from "moment";
import RenderDateTimePicker from "./RenderDatePicker";
import RadioButton from "./RadioButton";
import "react-datepicker/dist/react-datepicker.css";

import "react-widgets/dist/css/react-widgets.css";

class Form2 extends Component {
  constructor() {
    super();

    this.state = {
      startDate: new Date("1997-06-06"),
      country: [
        "South Africa",
        "Zambia",
        "Botswana",
        "Swaziland",
        "Lesotho",
        "Tanzania",
        "Mozambique"
      ]
    };
  }

  render() {
    const { previousPage, handleSubmit } = this.props;

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <div className="field">
            <Field
              class="Field"
              name="city"
              component={renderField}
              type="city"
              label="City"
              placeholder="City"
            />
          </div>
          <select class="option">
            {this.state.country.map(country => (
              <option> {country} </option>
            ))}
          </select>

          <Field
            name="dob"
            showTime={true}
            component={RenderDateTimePicker}
            label="Dob"
          />

          <div>
            <label>Sex</label>
            <div className="radio-button">
              <Field
                name="sex"
                component={RadioButton}
                label="male"
                type="radio"
                value="male"
              />
              <Field
                name="sex"
                component={RadioButton}
                type="radio"
                label="female"
                value="female"
              />
            </div>
          </div>

          <button type="button" className="btn" onClick={previousPage}>
            Previous
          </button>
          <button type="submit" className="btn">
            Next
          </button>
        </form>
      </div>
    );
  }
}

export default reduxForm({
  form: "Form2", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form2);
