import React, { Component } from "react";
import validate from "../../Utils/validations/form2Validations";
import renderField from "./RenderField";
import { reduxForm, Field } from "redux-form";
import RenderDateTimePicker from "./RenderDatePicker";
import RadioButtons from "./RadioButton";
import { RadioButton, RadioButtonGroup } from "material-ui/RadioButton";

import "react-datepicker/dist/react-datepicker.css";

import "react-widgets/dist/css/react-widgets.css";

class Form2 extends Component {
  constructor() {
    super();

    this.state = {
      startDate: new Date("1997-06-06"),
      countries: [
        "South Africa",
        "Zambia",
        "Botswana",
        "Swaziland",
        "Lesotho",
        "Tanzania",
        "Mozambique"
      ]
    };
  }

  render() {
    const { previousPage, handleSubmit } = this.props;

    return (
      <div>
        <form onSubmit={handleSubmit}>
          <div className="field">
            <Field
              name="options"
              countries={this.state.countries}
              component="select"
              type="select"
              label="Country"
            >
              <option />
              {this.state.countries.map(country => (
                <option value={country}> {country} </option>
              ))}
            </Field>
            <Field
              class="Field"
              name="city"
              component={renderField}
              type="text"
              label="City"
              placeholder="City"
            />
          </div>

          <Field
            name="dob"
            showTime={true}
            component={RenderDateTimePicker}
            label="Dob"
          />

          <label>Sex</label>
          <Field component={RadioButtons} className="radio-button">
            <RadioButton name="sex" label="male" type="radio" value="male" />
            <RadioButton
              name="sex"
              type="radio"
              label="female"
              value="female"
            />
          </Field>

          <button type="button" className="btn" onClick={previousPage}>
            Previous
          </button>
          <button type="submit" className="btn">
            Next
          </button>
        </form>
      </div>
    );
  }
}

export default reduxForm({
  form: "form2", // <------ same form name
  destroyOnUnmount: false, // <------ preserve form data
  forceUnregisterOnUnmount: true, // <------ unregister fields on unmount
  validate
})(Form2);
